import React, { useState ,useEffect} from 'react';
import { Camera, Clock, Archive, ChevronLeft, ChevronRight } from 'lucide-react';
import './App.css'
import logo from './media/lisual-logo.png';
import { collection, addDoc, getDocs } from "firebase/firestore";
import { db } from './firebase-config';
import { BrowserRouter, Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';

const ConstructionDashboard = () => {
  const [activeSection, setActiveSection] = useState('live');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [objects, setObjects] = useState([]);
  const [userdata, setUserData] = useState({});
  const [selectedDate, setSelectedDate] = useState(new Date());

  const sections = [
    { id: 'live', name: 'Live Camera', icon: Camera },
    { id: 'timelapse', name: 'Timelapse', icon: Clock },
    { id: 'archive', name: 'Footage Archive', icon: Archive },
  ];

  const location = useLocation();
  const word = location.state?.keyword || 'No word provided';

  const fetchPost = async () => {     
    await getDocs(collection(db, "usuarios"))
        .then((querySnapshot)=>{               
            const newData = querySnapshot.docs
                .map((doc) => ({...doc.data(), id:doc.id }));
            setObjects(newData);     
            newData.forEach(element => {
              console.log(element.identifier);
              if(element.identifier == word){
                setUserData(element);
              } 
            });      
        })
  } 


  useEffect(()=>{
    fetchPost();
  }, [])

  const renderContent = () => {
    switch (activeSection) {
      case 'live':
        return (
          <div className="card">
            <div className="card-header">
              <h1>Bienvenido {userdata.owner}</h1>
              <h2 className="card-title">Live Camera Feed</h2>
            </div>
            <div className="card-content">
              <iframe 
                width="100%" 
                height="600" 
                src={userdata.livecamURL}
                frameBorder="0" 
                allowFullScreen
                title="Live Camera Feed"
              ></iframe>
            </div>
          </div>
        );
      case 'timelapse':
        return (
          <div className="card">
            <div className="card-header">
              <h2 className="card-title">Construction Progress Timelapse</h2>
            </div>
            <div className="card-content">
              <div className="placeholder">
                <Clock size={48} />
                <p>Timelapse player would go here</p>
              </div>
            </div>
          </div>
        );
      case 'archive':
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-6">Archivo Fotografico</h2>
            <div className="flex space-x-4">
              <Calendar
                mode="single"
                selected={selectedDate}
                onSelect={setSelectedDate}
                className="rounded-md border"
              />
              <div className="flex-1 bg-white rounded-lg shadow p-4">
                <h3 className="text-lg font-semibold mb-2">Photos for {selectedDate.toDateString()}</h3>
                {/* Placeholder for photos */}
                <div className="grid grid-cols-3 gap-4">
                  {[1, 2, 3, 4, 5, 6].map((i) => (
                    <div key={i} className="bg-gray-200 h-32 rounded-md"></div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-content">
          {/*<img src={logo} className={`logo-sidebar`}></img>*/}
          <h1 className="dashboard-title">Dashboard</h1>
          <nav>
            {sections.map((section) => (
              <button
                key={section.id}
                className={`nav-btn ${activeSection === section.id ? 'active' : ''}`}
                onClick={() => setActiveSection(section.id)}
              >
                <section.icon className="icon" size={24} />
                <span className="btn-text">{section.name}</span>
              </button>
            ))}
          </nav>
        </div>
        <button
          className="toggle-btn"
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? <ChevronLeft size={24} /> : <ChevronRight size={24} />}
        </button>
      </div>

      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default ConstructionDashboard;
