import React, { useState, useEffect } from 'react';
import { Bell, ChevronDown, Camera, Clock, Archive, ChevronLeft, ChevronRight, Home, Users, Settings, CreditCard, HelpCircle } from 'lucide-react';
import Calendar from './Calendar.js';
import './App.css'
import logo from './media/lisual-logo.png';
import { collection, addDoc, getDocs } from "firebase/firestore";
import { db } from './firebase-config';
import { BrowserRouter, Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';

const ConstructionDashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('Dashboard');
  const [objects, setObjects] = useState({})
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [userdata, setUserData] = useState({})

  const location = useLocation();
  const navigate = useNavigate();
  const word = location.state?.keyword || 'No word provided';

  const fetchPost = async () => {     
    await getDocs(collection(db, "usuarios"))
        .then((querySnapshot)=>{               
            const newData = querySnapshot.docs
                .map((doc) => ({...doc.data(), id:doc.id }));
            setObjects(newData);     
            newData.forEach(element => {
              console.log(element.identifier);
              if(element.identifier == word){
                setUserData(element);
              } 
            });      
        })
  } 

  useEffect(()=>{
    fetchPost();
  }, [])

  const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);

  const renderContent = () => {
    // ... (keep this function as is)
  };

  const menuItems = [
    { name: 'Dashboard', icon: <Home size={20} /> },
    { name: 'Imagen en vivo', icon: <Camera size={20} /> },
    { name: 'Archivo Fotografico', icon: <Archive size={20} /> },
    { name: 'Inversores', icon: <Users size={20} /> },
    { name: 'Opciones', icon: <Settings size={20} /> },
    { name: 'Pago', icon: <CreditCard size={20} /> },
    { name: 'Cuenta', icon: <Users size={20} /> },
    { name: 'Ayuda', icon: <HelpCircle size={20} /> }
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`${isSidebarOpen ? 'w-64' : 'w-20'} bg-blue-600 text-white p-4 transition-all duration-300 ease-in-out`}>
        <div className="mb-8 flex items-center justify-between">
          {isSidebarOpen && <h1 className="text-2xl font-bold">LISUAL Pro</h1>}
          <button onClick={toggleSidebar} className="p-2 rounded-full hover:bg-blue-700">
            {isSidebarOpen ? <ChevronLeft /> : <ChevronRight />}
          </button>
        </div>
        <nav>
          <ul className="space-y-2">
            {menuItems.map((item) => (
              <li
                key={item.name}
                className={`p-2 rounded cursor-pointer ${activeTab === item.name ? 'bg-blue-700' : 'hover:bg-blue-500'} ${!isSidebarOpen ? 'flex justify-center' : ''}`}
                onClick={() => setActiveTab(item.name)}
              >
                {isSidebarOpen ? (
                  <div className="flex items-center">
                    {item.icon}
                    <span className="ml-2">{item.name}</span>
                  </div>
                ) : (
                  item.icon
                )}
              </li>
            ))}
          </ul>
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top bar */}
        <header className="bg-white shadow-sm p-4 flex items-center justify-end">
          <div className="flex items-center">
            <Bell className="mr-4 text-gray-500" />
            <div 
              className="flex items-center cursor-pointer"
              onClick={() => navigate('/profile')}
            >
              <div className="w-8 h-8 rounded-full bg-gray-300 flex items-center justify-center text-gray-600 font-bold mr-2">
                J
              </div>
              <span className="mr-2">{userdata.user}</span>
              <ChevronDown className="text-gray-500" />
            </div>
          </div>
        </header>

        {/* Dashboard content */}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100 p-6">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default ConstructionDashboard;
