import React, { useState ,useEffect} from 'react';
import { Search, Bell, ChevronDown, Menu, Camera, Clock, Archive, ChevronLeft, ChevronRight } from 'lucide-react';
import { Calendar } from '@/components/ui/calendar';
import './App.css'
import logo from './media/lisual-logo.png';
import { collection, addDoc, getDocs } from "firebase/firestore";
import { db } from './firebase-config';
import { BrowserRouter, Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';

const ConstructionDashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('Dashboard');
  const [objects, setObjects] = useState({})
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [userdata, setUserData] = useState({})

  const location = useLocation();
  const word = location.state?.keyword || 'No word provided';

  const fetchPost = async () => {     
    await getDocs(collection(db, "usuarios"))
        .then((querySnapshot)=>{               
            const newData = querySnapshot.docs
                .map((doc) => ({...doc.data(), id:doc.id }));
            setObjects(newData);     
            newData.forEach(element => {
              console.log(element.identifier);
              if(element.identifier == word){
                setUserData(element);
              } 
            });      
        })
  } 


  useEffect(()=>{
    fetchPost();
  }, [])

  const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);

  const renderContent = () => {
    switch (activeTab) {
      case 'Archivo Fotografico':
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-6">Archivo Fotografico</h2>
            <div className="flex space-x-4">
              <Calendar
                mode="single"
                selected={selectedDate}
                onSelect={setSelectedDate}
                className="rounded-md border"
              />
              <div className="flex-1 bg-white rounded-lg shadow p-4">
                <h3 className="text-lg font-semibold mb-2">Photos for {selectedDate.toDateString()}</h3>
                {/* Placeholder for photos */}
                <div className="grid grid-cols-3 gap-4">
                  {[1, 2, 3, 4, 5, 6].map((i) => (
                    <div key={i} className="bg-gray-200 h-32 rounded-md"></div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        );
      default:
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-6">{activeTab}</h2>
            <div className="bg-white rounded-lg shadow p-4">
              <img 
                src="/api/placeholder/800/400" 
                alt="Construction site" 
                className="w-full h-64 object-cover rounded"
              />
            </div>
          </div>
        );
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`${isSidebarOpen ? 'w-64' : 'w-20'} bg-blue-600 text-white p-4 transition-all duration-300 ease-in-out`}>
        <div className="mb-8 flex items-center justify-between">
          {isSidebarOpen && <h1 className="text-2xl font-bold">LISUAL Pro</h1>}
          <button onClick={toggleSidebar} className="p-2 rounded-full hover:bg-blue-700">
            {isSidebarOpen ? <ChevronLeft /> : <ChevronRight />}
          </button>
        </div>
        <nav>
          <ul className="space-y-2">
            {['Dashboard', 'Imagen en vivo', 'Archivo Fotografico', 'Inversores', 'Opciones', 'Pago', 'Cuenta', 'Ayuda'].map((item) => (
              <li
                key={item}
                className={`p-2 rounded cursor-pointer ${activeTab === item ? 'bg-blue-700' : 'hover:bg-blue-500'}`}
                onClick={() => setActiveTab(item)}
              >
                {isSidebarOpen ? item : item.charAt(0)}
              </li>
            ))}
          </ul>
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top bar */}
        <header className="bg-white shadow-sm p-4 flex items-center justify-between">
          <div className="flex items-center w-1/2">
            <Menu className="mr-4 text-gray-500" />
            <div className="relative w-full">
              <input
                type="text"
                placeholder="Buscar..."
                className="w-full pl-10 pr-4 py-2 rounded-lg border focus:outline-none focus:border-blue-500"
              />
              <Search className="absolute left-3 top-2.5 text-gray-400" />
            </div>
          </div>
          <div className="flex items-center">
            <Bell className="mr-4 text-gray-500" />
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-full bg-gray-300 flex items-center justify-center text-gray-600 font-bold mr-2">
                J
              </div>
              <span className="mr-2">{userdata.user}</span>
              <ChevronDown className="text-gray-500" />
            </div>
          </div>
        </header>

        {/* Dashboard content */}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100 p-6">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default ConstructionDashboard;
